import React, { Component } from "react";
import "./style.css";
import API from "../../utils/API";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


//import { SSL_OP_ALLOW_UNSAFE_LEGACY_RENEGOTIATION } from "constants";
const cardPostion = {
  "float": "left",
}

class Example extends Component {
  state = {show:true}
  setShow = (newShow) => {
    this.setState({
      [show]: newShow
    });
  }
  handleClose = () => this.setShow(false);
  handleShow = () => this.setShow(true);

  render() {
    return (
      <>
        <Button variant="primary" onClick={this.handleShow}>
          Launch demo modal
          </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
              </Button>
            <Button variant="primary" onClick={this.handleClose}>
              Save Changes
              </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

class PostForm extends Component {

  state = {
    item: "",
    quantity: "",
    category: "",
    Address: "",
    stock_id: "",
    stock_arr: [],
    uploading: false,
    url: ""
  }
  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };
  deletePost = id => {
    if (id !== undefined) {
      //console.log("inside delete"+id);
      API.deleteData(id)
        .then(res => {
          this.setState({ stock_arr: "", item: "", quantity: "", category: "", Address: "", url: "" });
          //console.log(res.data+"successfully deleted");
          alert("successfully deleted post");
          //console.log(this.state.stock_arr.itemName);
        }

        )
        .catch(err => console.log(err));
    }

  };

  getDataById = id => {
    API.getDataById(id)
      .then(res => {
        this.setState({ stock_arr: res.data, item: "", quantity: "", category: "", Address: "" });
        console.log("graaaaaaaaaaab", res.data);
        //console.log(this.state.stock_arr.itemName);
      }

      )
      .catch(err => console.log(err));
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.item && this.state.quantity) {
      const newPost = {
        itemName: this.state.item,
        quantity: this.state.quantity,
        category: this.state.category,
        Address: this.state.Address,
        url: this.state.url,
      }
      console.log("newPost", newPost);
      API.savePost(newPost)
        .then(res => {
          console.log(res.data);
          this.setState({ stock_id: res.data._id })
          this.getDataById(res.data._id);
        })
        .catch(err => console.log(err));
    }

  };

  render(){
    return (<Example />);
  };
}




