            <div className="container">
                    {/* <h2 className="maintext">Reduce, Reuse, Recycle</h2> */}
                    <h5 className="secondarytext">Reduce, Reuse, Recycle | Share items you want to donate or claim products you need for free!</h5>
                    <a href="/donate"><button type="button" className="navbutton btn btn-warning btn-lg">Donate</button></a>
                    <img src="https://cdn.dribbble.com/users/470545/screenshots/3607374/moving-drib.gif" class="rounded float-left" alt="..." width="500px" height="430px" />
            </div>
        </div>
    );
}

export default Jumbotron;
