import React, { Component } from 'react'
// import { API_URL } from './config'
import PostForm from "../PostForm"
import API from '../../utils/API';

class UploadImg extends Component {

  state = {
    uploading: false,
    imageUrl: ""
  }

  onChange = e => {
    const files = Array.from(e.target.files)
    this.setState({ uploading: true })

    const formData = new FormData()

    files.forEach((file, i) => {
      formData.append(i, file)
    })

    //sends the img to server
    fetch(`http://localhost:3001/image-upload`, {
      method: 'POST',
      body: formData
    })
    .then(res => res.json())
    .then(images => {
      this.setState({ 
        uploading: false,
        imageUrl: images[0].url
      });
    });
  }

  // submitting this.state.imageURL to the API
  // onSubmit = (e) => {
  //   console.log("Submitting");
  //   API.saveItem({
  //             name: "SomeItemName",
  //       description: "SomeDescription",
  //       location: "Random location",
  //       imageUrl: this.state.imageUrl
  //   });
  // }

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.item && this.state.quantity) {
      API.savePost({
        itemName: this.state.item,
        quantity: this.state.quantity,
        category: this.state.category,
        imageUrl: this.state.imageUrl
      }).then(res => console.log(res))
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <div>
          <PostForm onChange={this.handleInputChange} image={this.state.imageUrl}></PostForm>
      </div>
      //adding this.state.imageUrl
    )
  }
}

export default UploadImg;
